import React from 'react'
import './ExploreMenu.css'
import {menu_list} from '../../assets/assets'

/*all the relevent data and the array list is in assests.js file*/
/*this needs to be maped in the backend to show the data dynamically*/

const ExploreMenu = ({category, setCategory}) => {
  return (
    <div className="explore-menu" id='explore-menu'>
        <h1>Explore Our Menu</h1>
        <p className='explore-menu-text'>Choose from a diverse menu featuring a delectable array of dishes. Our mission is to satisfy your cravings and elevate your dining experience, one delicious meal at a time.</p>
        <div className="explore-menu-list">
            {menu_list.map((item,index)=>{
                return(
                    /*a temp var category is used to store the current user clicked menu item this will be used to show the data similar to the category clicked*/
                    <div onClick={() => setCategory(prev=> prev === item.menu_name ? 'All' : item.menu_name)} key={index} className="explore-menu-list-item">
                        <img className={category=== item.menu_name? "active" : ""} src={item.menu_image} alt='' />
                        <p>{item.menu_name}</p>
                    </div>
                )
            })}
        </div>
        <hr/>
    </div>
  )
}

export default ExploreMenu
